package com.JumpStatement;

public class BreakAndContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	for (int i=1;i<=5;i++)
	{
		if(i==3)
		{
			System.out.println("Anthati");
			
		}
		

	}
	System.out.println("***break keyword***");
	for(int i=1;i<=5;i++)
	{
		if(i==3)
		{
			break;
			
			
		}
		System.out.println(i+"jashwanth");
		
	}
	 	System.out.println("****continue keyword");
	 	for(int i=1;i<=5;i++)
	 	{
	 		if(i==3)
	 		{
	 			continue;
	 			
	 		}
	 		System.out.println(i+"goud");
	 	
	 	}
	}

}
